import React, { useState, useEffect } from 'react';
import { createProject, updateProject, getProjectById } from '../api/api';
import { useHistory, useParams } from 'react-router-dom';

const ProjectForm = ({ isEditing }) => {
        const [project, setProject] = useState({ name: '', description: '', status: 'New' });
        const [error, setError] = useState('');
        const history = useHistory();
        const { id } = useParams();

        useEffect(() => {
            if (isEditing && id) {
                getProjectById(id)
                    .then(response => setProject(response.data))
                    .catch(error => setError(error.message));
            }
        }, [isEditing, id]);

        const handleChange = (e) => {
            const { name, value } = e.target;
            setProject({...project, [name]: value });
        };

        const handleSubmit = (e) => {
            e.preventDefault();
            if (isEditing) {
                updateProject(id, project)
                    .then(() => history.push(`/projects/${id}`))
                    .catch(error => setError(error.message));
            } else {
                createProject(project)
                    .then(() => history.push('/'))
                    .catch(error => setError(error.message));
            }
        };

        return ( <
            div >
            <
            h1 > { isEditing ? 'Edit Project' : 'New Project' } < /h1> {
                error && < p > { error } < /p>} <
                    form onSubmit = { handleSubmit } >
                    <
                    div >
                    <
                    label > Name < /label> <
                    input type = "text"
                name = "name"
                value = { project.name }
                onChange = { handleChange }
                required / >
                    <
                    /div> <
                    div >
                    <
                    label > Description < /label> <
                    textarea name = "description"
                value = { project.description }
                onChange = { handleChange }
                required > < /textarea> <
                    /div> <
                    div >
                    <
                    label > Status < /label> <
                    select name = "status"
                value = { project.status }
                onChange = { handleChange } >
                    <
                    option value = "New" > New < /option> <
                    option value = "In Progress" > In Progress < /option> <
                    option value = "Completed" > Completed < /option> <
                    /select> <
                    /div> <
                    button type = "submit" > { isEditing ? 'Update' : 'Create' } < /button> <
                    /form> <
                    /div>
            );
        };

        export default ProjectForm;
